import React, { Component } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import magnifiertool from './img/magnifier-tool.png'

import './App.css';

class App extends Component {
  state = {
    address: '',
    location: '',
    weatherData: ''
  };

  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/api/weather', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: { address: this.state.address },
    });
    // const body = await response.text();
    console.log(body)
    this.setState({ 
      weatherData: JSON.stringify(body.forecast),
      location: JSON.stringify(body.location)
    });
    console.log(this.state);
  };

render() {
    return (
      <div className="main-content">
        <Header title={"Weather"} />
        <form class="search-form" onSubmit={this.handleSubmit}>
            <input value={this.state.address} onChange={e => this.setState({ address: e.target.value })} class="search-input" type="text" />
            <input img class="search-button" onClick={this.handleSubmit} src={magnifiertool} type="image" alt="submit" 
            style={{width: "36px"}}/> 
        </form>
        <Footer />
      </div>
    );
  }
}

export default App;
